class i{constructor(){this.init()}init(){this.handleNotifications(),this.handleForms()}handleNotifications(){document.querySelectorAll(".notification.show").forEach(e=>{setTimeout(()=>{e.classList.remove("show"),setTimeout(()=>e.remove(),300)},5e3)})}handleForms(){document.querySelectorAll('form[method="POST"]').forEach(o=>{const e=o.querySelector('input[name="_method"]');e&&e.value==="DELETE"&&o.addEventListener("submit",t=>{confirm("هل أنت متأكد من هذا الإجراء؟")||t.preventDefault()})})}showNotification(o,e="success"){const t=document.createElement("div");t.className=`notification ${e} show`,t.textContent=o,document.body.appendChild(t),setTimeout(()=>{t.classList.remove("show"),setTimeout(()=>t.remove(),300)},5e3)}}document.addEventListener("DOMContentLoaded",function(){window.dashboardBase=new i});
