class o{constructor(){this.animateStats()}animateStats(){document.querySelectorAll(".stat-number").forEach(e=>{const t=parseInt(e.textContent);if(isNaN(t))return;let n=0;const r=t/50,a=setInterval(()=>{n+=r,n>=t?(e.textContent=t,clearInterval(a)):e.textContent=Math.floor(n)},30)})}}document.addEventListener("DOMContentLoaded",function(){document.querySelector(".stats-grid")&&new o});
