class o{constructor(){this.handleStatusUpdates()}handleStatusUpdates(){document.querySelectorAll(".status-select").forEach(t=>{const e=t.value;t.addEventListener("change",function(n){const s=this.value,a=this.options[this.selectedIndex].text;if(s!==e&&!confirm(`هل أنت متأكد من تغيير حالة الحجز إلى "${a}"؟`))return this.value=e,n.preventDefault(),!1})})}}document.addEventListener("DOMContentLoaded",function(){document.querySelector(".filters-container")&&new o});
